mapper.update:
UPDATE posts
SET title = #{title},
	content = #{content},
	updated_date = SYSTIMESTAMP, 
	state = #{state} 
WHERE post_id = #{postId} AND user_num = #{userNum}

mapper.select:
SELECT p.post_id AS postId, p.user_num AS userNum, p.community_id AS communityId, p.title, p.content, p.post_type AS postType, p.view_count AS viewCount, p.like_count AS likeCount, p.created_date AS createdDate, p.updated_date AS updatedDate, p.state, u.userName AS authorNickname,
NULL AS authorProfileImage
FROM posts p JOIN member1 u ON p.user_num = u.memberIdx WHERE p.post_id = #{postId}
