mapper.select:
SELECT p.post_id AS postId, p.user_num AS userNum, p.community_id AS communityId, p.title, p.content, p.post_type AS postType, p.view_count AS viewCount, p.like_count AS likeCount, p.created_date AS createdDate, p.updated_date AS updatedDate, p.state, p.reply_enabled AS replyEnabled, p.show_counts AS showCounts, u.user_nickname AS authorNickname,
NULL AS authorProfileImage
FROM posts p JOIN member u ON p.user_num = u.user_num WHERE p.post_id = #{postId}

mapper.insert:
INSERT INTO FILEAT (
file_at_id, file_name, file_path, file_size,
file_type, file_order, uploaded_date, post_id
) VALUES (
fileat_seq.NEXTVAL, #{fileName}, #{filePath}, #{fileSize},
#{fileType}, #{fileOrder}, SYSTIMESTAMP, #{postId}
)

mapper.update:
UPDATE posts
SET view_count = view_count + 1
WHERE post_id = #{postId}
