mapper.update:
UPDATE posts
SET title = #{title}, content = #{content},
updated_date = SYSTIMESTAMP, state = #{state},
reply_enabled = #{replyEnabled}, show_counts = #{showCounts}
WHERE post_id = #{postId} AND user_num = #{userNum}

mapper.insert:
INSERT INTO FILEAT (
file_at_id, file_name, file_path, file_size,
file_type, file_order, uploaded_date, post_id
) VALUES (
fileat_seq.NEXTVAL, #{fileName}, #{filePath}, #{fileSize},
#{fileType}, #{fileOrder}, SYSTIMESTAMP, #{postId}
)

mapper.update:
UPDATE COMMENTS
SET CONTENT = #{content}, UPDATED_DATE = SYSDATE
WHERE COMMENT_ID = #{commentId} AND USER_NUM = #{userNum}
