sql.if:
(( TO_CHAR(noti_reg_date, 'YYYY-MM-DD') = #{kwd}
	OR TO_CHAR(noti_reg_date, 'YYYYMMDD') = #{kwd} ) OR
( TO_CHAR(update_date, 'YYYY-MM-DD') = #{kwd}
	OR TO_CHAR(update_date, 'YYYYMMDD') = #{kwd} ))

mapper.select:
SELECT noti.notice_num notice_num, noti.state state, noti.noti_title noti_title, memb.USER_NICKNAME user_nickname, TO_CHAR(noti.noti_reg_date, 'YYYY-MM-DD') noti_reg_date, noti.noti_hitcount noti_hitcount FROM NOTICE noti, member memb
is_notice = #{is_notice}
AND noti.state = #{state}
AND
AND noti.USER_NUM = memb.USER_NUM
ORDER BY noti.noti_reg_date DESC, noti.notice_num DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT NVL(COUNT(*), 0)
FROM notice noti
JOIN member memb ON noti.user_num = memb.user_num


		is_notice = #{is_notice}


		AND 


		AND noti.state = #{state}
