mapper.update:
UPDATE community SET com_name = #{com_name}, com_description = #{com_description}, is_private = #{is_private}, 
	icon_image = #{icon_image, jdbcType=VARCHAR}, 
	banner_image = #{banner_image, jdbcType=VARCHAR}, category_id = #{category_id} 
WHERE community_id = #{community_id}

mapper.select:
	    SELECT 
	        c.community_id, c.com_name, c.com_description, c.icon_image, c.banner_image, c.is_private, 
	        c.member_count, c.post_count, TO_CHAR(c.created_date, 'YYYY-MM-DD') created_date, 
	        c.today_visit, c.total_visit, c.user_num, c.category_id
	    FROM community c


	            INNER JOIN community_favorites f ON c.community_id = f.community_id
	            WHERE f.user_num = #{user_num}


				WHERE c.user_num = #{user_num}
					OR c.community_id IN (
						SELECT community_id FROM community_follow 
						WHERE user_num = #{user_num}
						UNION
           				SELECT community_id FROM community_favorites 
           				WHERE user_num = #{user_num}
					)



		ORDER BY c.community_id DESC
